import React from 'react';
import { useHistory } from 'react-router-dom';
import { GameType } from '../../interfaces/rootInterface';
import './styles.css';

interface GameItemFreePropsType {
  game: GameType;
  heightImage?: string;
}

function GameItemFree({ game, heightImage }: GameItemFreePropsType) {
  const history = useHistory();
  const { nameGame, discount, imageGameDetail, genres } = game;
  const displayImage =
    imageGameDetail[0].url ||
    'data:image/jpeg;base64,';
  const renderGenre = () => {
    if (!genres) return null;
    return genres.map(genre => (
      <p className='game-item__detail-wrapper__type'>
        {genre.idGenreNavigation.nameGenre}
      </p>
    ));
  };
  return (
    <div
      className='game-item game-item--free'
      onClick={() => {
        history.push('/game/' + game.idGame);
      }}>
      <div
        style={heightImage ? { height: heightImage } : {}}
        className='game-item__image game-item__image--free'>
        <div className='game-item__tag-free'>free now</div>
        <img
          className='game-item__image'
          src={displayImage}
          alt='game-item-free'
        />
        <div className='game-item-free__tag'>free now</div>
      </div>
      <div className='game-item-free__description'>
        <p className='game-item-free__description__title'>{nameGame}</p>
        <p className='game-item-free__description__subtitle'>
          Free now 12:45AM Sep 14 2020
        </p>
      </div>
    </div>
  );
}

export default GameItemFree;
